INSERT INTO `wp_options` VALUES (643,'dbem_waitlists_text_already_waiting','<p>\r\n	#_EVENTNAME is fully booked. You have already joined the waiting list for this event.	\r\n</p>\r\n<p>\r\n	You will be notified when a space becomes available for you to book.	\r\n</p>\r\n<p>\r\n	You are number #_WAITLIST_BOOKING_POSITION in line.	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>\r\n<p>\r\n	If you do not want to attend this event anymore, please cancel your reservation so others can have an opportunity to attend this event.	\r\n</p>','yes');
INSERT INTO `wp_options` VALUES (644,'dbem_waitlists_text_booking_form','<p>\r\n	You are currently on a waiting list and are now elegible to book the following event:	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>\r\n{has_waitlist_expiry}\r\n<p>\r\n	Your requested spaces will be reserved for #_WAITLIST_BOOKING_EXPIRY.	\r\n</p>\r\n{/has_waitlist_expiry}\r\n<p>\r\n	If you do not want to attend this event anymore, please cancel your reservation so others can have an opportunity to attend this event.	You can also cancel your booking by clicking the button below.	\r\n</p>','yes');
INSERT INTO `wp_options` VALUES (645,'dbem_waitlists_text_form','<p>\r\n	This event is fully booked. You can join a waitlist and if an elegible ticket becomes available, you will be notified by email to make a booking.	\r\n</p>\r\n{has_waitlist_limit}\r\n<p>\r\n	There are #_WAITLIST_AVAILABLE spaces left on the waitlist.\r\n</p>\r\n{/has_waitlist_limit}\r\n{has_waitlist_booking_limit}\r\n<p>\r\n	You can book up to #_WAITLIST_BOOKING_LIMIT spaces on this waitlist.\r\n</p>\r\n{/has_waitlist_booking_limit}\r\n{has_waitlist_expiry}\r\n<p>\r\n	Please remember that you have #_WAITLIST_EXPIRY hours to book reserved spaces once they become available, you will be notified immediately when an elegible ticket becomes available.\r\n</p>\r\n{/has_waitlist_expiry}','yes');
INSERT INTO `wp_options` VALUES (646,'dbem_waitlists_text_cancelled','<p>\r\n	You have cancelled your waitlist reservation for this event.	\r\n</p>\r\n<p>\r\n	Thank you for cancelling your waitlist reservation, so that others can have an opportunity to attend the event!	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n	\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>','yes');
INSERT INTO `wp_options` VALUES (647,'dbem_waitlists_text_expired','<p>\r\n	Your wait-listed reservation for #_EVENTNAME expired #_WAITLIST_BOOKING_EXPIRED ago. You will need to re-apply for the waiting lists if you wish to book again.	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>','yes');
