(642, 'dbem_waitlists_login_text', 'Please log in to access the waitlist.', 'yes'),
(643, 'dbem_waitlists_text_already_waiting', '<p>\r\n	#_EVENTNAME is fully booked. You have already joined the waiting list for this event.	\r\n</p>\r\n<p>\r\n	You will be notified when a space becomes available for you to book.	\r\n</p>\r\n<p>\r\n	You are number #_WAITLIST_BOOKING_POSITION in line.	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>\r\n<p>\r\n	If you do not want to attend this event anymore, please cancel your reservation so others can have an opportunity to attend this event.	\r\n</p>', 'yes'),
(644, 'dbem_waitlists_text_booking_form', '<p>\r\n	You are currently on a waiting list and are now elegible to book the following event:	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>\r\n{has_waitlist_expiry}\r\n<p>\r\n	Your requested spaces will be reserved for #_WAITLIST_BOOKING_EXPIRY.	\r\n</p>\r\n{/has_waitlist_expiry}\r\n<p>\r\n	If you do not want to attend this event anymore, please cancel your reservation so others can have an opportunity to attend this event.	You can also cancel your booking by clicking the button below.	\r\n</p>', 'yes'),
(645, 'dbem_waitlists_text_form', '<p>\r\n	This event is fully booked. You can join a waitlist and if an elegible ticket becomes available, you will be notified by email to make a booking.	\r\n</p>\r\n{has_waitlist_limit}\r\n<p>\r\n	There are #_WAITLIST_AVAILABLE spaces left on the waitlist.\r\n</p>\r\n{/has_waitlist_limit}\r\n{has_waitlist_booking_limit}\r\n<p>\r\n	You can book up to #_WAITLIST_BOOKING_LIMIT spaces on this waitlist.\r\n</p>\r\n{/has_waitlist_booking_limit}\r\n{has_waitlist_expiry}\r\n<p>\r\n	Please remember that you have #_WAITLIST_EXPIRY hours to book reserved spaces once they become available, you will be notified immediately when an elegible ticket becomes available.\r\n</p>\r\n{/has_waitlist_expiry}', 'yes'),
(646, 'dbem_waitlists_text_cancelled', '<p>\r\n	You have cancelled your waitlist reservation for this event.	\r\n</p>\r\n<p>\r\n	Thank you for cancelling your waitlist reservation, so that others can have an opportunity to attend the event!	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n	\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>', 'yes'),
(647, 'dbem_waitlists_text_expired', '<p>\r\n	Your wait-listed reservation for #_EVENTNAME expired #_WAITLIST_BOOKING_EXPIRED ago. You will need to re-apply for the waiting lists if you wish to book again.	\r\n</p>\r\n<p>\r\n	<strong>Waitlist Reservation Details:</strong>\r\n\r\n</p>\r\n<p>\r\n	<strong>Event :</strong> #_EVENTNAME<br>\r\n	<strong>Date/Time :</strong> #_EVENTDATES @ #_EVENTTIMES<br>\r\n	<strong>Reserved Spaces :</strong> #_BOOKINGSPACES\r\n</p>', 'yes'),
(648, 'dbem_waitlists_text_full', '<p>\r\n	This event is fully booked. You can join a waitlist and if an elegible ticket becomes available, you will be notified by email to make a booking.\r\n</p>\r\n<p>\r\n	There are no more spaces left available on the waitlist. Please check back later, as spaces may become available.	\r\n</p>', 'yes'),
(649, 'dbem_waitlists_feedback_confirmed', 'You have been added to the waitlist. You are #_WAITLIST_BOOKING_POSITION in line. You will be emailed if a ticket becomes available for booking, you will have #_WAITLIST_EXPIRY hours to make a booking before it is released to the next person in the list.', 'yes'),
(650, 'dbem_waitlists_feedback_already_waiting', 'You are already on the waitlist, you are position #_WAITLIST_BOOKING_POSITION out of #_WAITLIST_WAITING. You will be notified by email if a space becomes available.', 'yes'),
(651, 'dbem_waitlists_feedback_already_waiting_guest', 'You are already on the waitlist, You will be notified by email if a space becomes available.', 'yes'),
(652, 'dbem_waitlists_feedback_full', 'There are no more spaces left available on the waitlist. Please check back later, as spaces may become available.', 'yes'),
(653, 'dbem_waitlists_feedback_booking_limit', 'You cannot reserve more than #_WAITLIST_BOOKING_LIMIT spaces on the waitlist.', 'yes'),
(654, 'dbem_waitlists_feedback_spaces_limit', 'There are not enough available spaces on the waitlist, only #_WAITLIST_AVAILABLE space(s) available.', 'yes'),
(655, 'dbem_waitlists_feedback_log_in', 'You must log in or register to make a booking.', 'yes'),
(656, 'dbem_waitlists_feedback_cancelled', 'You have cancelled your waitlist reservation for this event. Thank you for cancelling your waitlist reservation, so that others can have an opportunity to attend the event!', 'yes') ;
