(659, 'dbem_waitlists_emails_confirmed_subject', 'Waitlist Confirmation - #_EVENTNAME', 'yes'),
(660, 'dbem_waitlists_emails_confirmed_message', 'Hello #_BOOKINGNAME\r\n\r\nYou have successfully reserved #_BOOKINGSPACES spaces for #_EVENTNAME on #_EVENTDATES at #_EVENTTIMES.\r\n\r\nYou are number #_WAITLIST_BOOKING_POSITION in line.\r\n{has_waitlist_expiry}\r\nPlease remember that you have #_WAITLIST_EXPIRY hours to book reserved spaces once they become available, you will be notified immediately when an elegible ticket becomes available.\r\n{/has_waitlist_expiry}\r\n{no_waitlist_expiry}\r\nYou will be notified immediately when an elegible ticket becomes available\r\n{/no_waitlist_expiry}\r\n\r\nIf you do not want to attend this event anymore, please cancel your reservation so others can have an opportunity to attend this event. You can view all the information about your reservation and cancel by following this link:\r\n\r\n#_WAITLIST_BOOKING_URL\r\n\r\nBest Regards,\r\n\r\n#_CONTACTNAME', 'yes'),
(661, 'dbem_waitlists_emails_approved_subject', 'Waitlist Approved, Book Now! - #_EVENTNAME', 'yes'),
(662, 'dbem_waitlists_emails_approved_message', 'Hello #_BOOKINGNAME\r\n\r\nGreat news! You are now able to book up to #_BOOKINGSPACES spaces for #_EVENTNAME on #_EVENTDATES at #_EVENTTIMES.\r\n\r\nIf you do not want to attend this event anymore, please cancel your reservation so others can have an opportunity to attend this event.\r\n\r\nPlease follow the following link to complete or cancel your booking:\r\n\r\n#_WAITLIST_BOOKING_URL\r\n\r\n{has_waitlist_expiry}\r\nPlease remember that you have #_WAITLIST_EXPIRY hours to complete this booking, otherwise your reservation will be cancelled and these spaces will be made available to the next person in line.{/has_waitlist_expiry}\r\n\r\nBest Regards,\r\n\r\n#_CONTACTNAME', 'yes'),
(663, 'dbem_waitlists_emails_expired_subject', 'Waitlist Booking Expired - #_EVENTNAME', 'yes'),
(664, 'dbem_waitlists_emails_expired_message', 'Hello #_BOOKINGNAME\r\n\r\nYour wait-listed reservation for #_EVENTNAME has expired. You will need to re-apply for the waiting lists if you wish to book again.\r\n\r\nPlease remember that you have #_WAITLIST_EXPIRY hours to book reserved spaces once they become available, you will be notified when that happens.\r\n\r\nBest Regards,\r\n\r\n#_CONTACTNAME', 'yes'),
(665, 'dbem_waitlists_emails_cancelled_subject', 'Waitlist Booking Cancelled - #_EVENTNAME', 'yes'),
