mage}[/events_list]`\ndocument.getElementById(\'featured-excerpt\').innerHTML = `[events_list category="featured" limit=1]#_EVENTEXCERPT[/events_list]`\n\n    // List of events\n    var today = new Date();\n    var future = new Date();\n    future.setDate(today.getDate() + 30);\n\n    today = today.toISOString().split(\'T\')[0];\n    future = future.toISOString().split(\'T\')[0];\n    var scope = `scope="${today}, ${future}"`;\n\n    document.getElementById(\'eventslist\').innerHTML = `[events_list ${scope} category="-featured" pagination=1 limit=3]{no_category_featured}<div><h4>#_EVENTLINK</h4><p>#_EVENTDATES, #_EVENTTIMES</p><p>#_EVENTEXCERPT</p></div>{/no_category_featured}[/events_list]`;\n</script>\n<!-- /wp:html -->\n\n<!-- wp:separator {"className":"is-style-wide"} -->\n<hr class="wp-block-separator has-alpha-channel-opacity is-style-wide"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading {"style":{"typography":{"fontSize":"48px"}}} -->\n<h2 class="wp-block-heading" style="font-size:48px">Het menu voor 28-08 en 30-08</h2>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class="wp-block-columns"><!-- wp:column -->\n<div class="wp-block-column"><!-- wp:heading {"fontSize":"medium"} -->\n<h2 class="wp-block-heading has-medium-font-size">Woensdag 28-08</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Een boerenomelet met brood.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {"id":1107,"width":"400px","sizeSlug":"full","linkDestination":"none","align":"center"} -->\n<figure class="wp-block-image aligncenter size-full is-resized"><img src="https://demijngang.nl/wp-content/uploads/2024/08/20240827_1.jpg" alt="" class="wp-image-1107" style="width:400px"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class="wp-block-column"><!-- wp:heading {"fontSize":"medium"} -->\n<h2 class="wp-block-heading has-medium-font-size">Vrijdag 30-08</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Een lekker pita broodje met shoarma, of een 