INSERT INTO `wp_em_events` VALUES (8,236,NULL,'rommelmarkt',1,NULL,1,'Vlooienmarkt, braderie met live muziek','2024-07-06','2024-07-06','12:00:00','23:59:00',NULL,'2024-07-06 10:00:00','2024-07-06 21:59:00','Europe/Amsterdam','Een echte ouderwetse rommelmarkt, waar alle buurtbewoners hun tweedehands spulletjes mogen verkopen om wat extra bij te verdienen. Reserveer een plekje voor €7,50- per plek van ca. 5 m² in-en-om ons gebouw.\r\n\r\nLet op: kraampjes of plekken met \"nieuwe spullen\" worden absoluut niet toegelaten op eigen terrein, deze worden ingedeeld in het stukje braderie indien mogelijk!',0,NULL,NULL,NULL,0,0,1,NULL,NULL,'2024-06-11 12:38:44','2024-07-06 23:59:24',0,NULL,NULL,NULL,NULL,0,NULL,0,NULL,'nl_NL',0);
INSERT INTO `wp_em_events` VALUES (9,237,NULL,'buurtfeest-2024',1,NULL,1,'Buurtfeest 2024','2024-08-17','2024-08-18','14:00:00','01:00:00',NULL,'2024-08-17 12:00:00','2024-08-17 23:00:00','Europe/Amsterdam','Op zaterdag 17 augustus komt de buurt weer samen op het vertrouwde speelveldje aan de Diamantstraat / Granaatstraat voor het langverwachte buurtfeest van 2024!\r\nHet belooft een dag vol plezier en vertier te worden voor jong en oud.\r\n\r\nDe festiviteiten beginnen al vroeg in de middag met een gezellige koffietafel, waar buurtgenoten elkaar kunnen ontmoeten en bijpraten.\r\nVoor de kinderen is er een uitgebreid programma met allerlei leuke activiteiten, waaronder een springkussen.\r\n\r\nLater op de avond wordt het tijd voor vertrouwde \"huis\" DJ Paul, in samenwerking met MK Audio. De muzikale sfeer zal de perfecte opmaat zijn voor de hoofdact van de avond: ANDERKOVVER!\r\nMet hun unieke mix van muzikaliteit, humor en een vleugje chaos beloven ze een avond vol energie en entertainment te bezorgen waar nog lang over nagepraat zal worden.',0,NULL,NULL,NULL,0,0,0,NULL,NULL,'2024-06-11 12:42:54','2024-08-18 01:07:05',0,NULL,NULL,NULL,NULL,0,NULL,0,NULL,'nl_NL',0);
