INSERT INTO `wp_ngg_gallery` VALUES (11,'2024-07-06-vlooienmarkt-amp-braderie','2024-07-06-vlooienmarkt-braderie','/wp-content/gallery/2024-07-06-vlooienmarkt-braderie/','2024-07-06 Vlooienmarkt &amp; Braderie','6 juli, 2024. \"We kijken terug op onze eerste braderie/rommelmarkt. Wat hebben we een leuke dag gehad en genoten van de live muziek!\r\n\r\nLangs deze weg willen we iedereen bedanken die mee heeft geholpen om deze dag tot een succes te maken:\r\n- MK Audio Sound, Light & More\r\n- Coverband \"Taking Cover\"\r\n- De Vries Transport B.V. Hoensbroek\r\n- Nico Descendre\r\n- Franssen Franken\r\n- De Tentenboer verhuur\r\n- HeJo Materialendepot\r\n- Grasmussch Parkstad Events & Verhuur\"',0,296,1,822);
INSERT INTO `wp_ngg_gallery` VALUES (12,'2024-08-18-buurtfeest-2024','2024-08-18-buurtfeest-2024','/wp-content/gallery/2024-08-18-buurtfeest-2024/','2024-08-18 Buurtfeest 2024','18 augustus, 2024. \"Ook ons team was aanwezig om een handje te helpen op het Buurtfeest 2024!!!  Top georganiseerd door buurtorganisatie GMS. Bedankt dat we hieraan deel mochten nemen, samen staan we sterk.  #SamenVoorDeBuurt #GMS \" - Grasmussch',0,382,1,918);
