INSERT INTO `wp_posts` VALUES (6,1,'2024-05-17 10:32:48','2024-05-17 10:32:48','<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"level\":1,\"style\":{\"typography\":{\"fontSize\":\"48px\"}}} -->\n<h1 class=\"wp-block-heading\" style=\"font-size:48px\">Welkom!</h1>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"fontSize\":\"medium\"} -->\n<h2 class=\"wp-block-heading has-medium-font-size\">Op de site van De MijnGang</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Stichting De MijnGang is gevestigd in het zuidelijkste puntje van Nederland, Heerlen; Heerlen-Noord om precies te zijn.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Wij zijn gestart met ons te richten op het sociale welzijn van ouderen in de buurt om zo eenzaamheid te minimaliseren. Samenzijn is na de coronacrisis niet meer vanzelfsprekend en al helemaal niet onder ouderen!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Samen is toch zoveel leuker dan alleen?</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"className\":\"hideme\",\"fontSize\":\"large\"} -->\n<h2 class=\"wp-block-heading hideme has-large-font-size\">&nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"className\":\"hideme\",\"fontSize\":\"medium\"} -->\n<h2 class=\"wp-block-heading hideme has-medium-font-size\">&nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":117,\"width\":\"600px\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized\"><img src=\"http://de-mijngang.local/wp-content/uploads/2024/05/00017-1024x437-1.webp\" alt=\"\" class=\"wp-image-117\" style=\"width:600px\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"66.66%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:66.66%\"><!-- wp:heading {\"fontSize\":\"medium\"} -->\n<h2 class=\"wp-block-heading has-medium-font-size\">Uitgelicht activiteit</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":1,\"style\":{\"typography\":{\"fontSize\":\"48px\"}}} -->\n<h1 class=\"wp-block-heading\" id=\"featured-title\" style=\"font-size:48px\">Geen uitgelicht activiteit</h1>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":27,\"scale\":\"cover\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"align\":\"center\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-style-default\" id=\"featured-image\"><img src=\"http://de-mijngang.local/wp-content/uploads/2024/05/Placeholder.png\" alt=\"Geen uitgelicht activiteit\" class=\"wp-image-27\" style=\"object-fit:cover\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:html -->\n<p id=\"featured-excerpt\">Op het moment is er geen uitgelicht activiteit.</p>\n<!-- /wp:html --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"33.33%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.33%\"><!-- wp:heading {\"fontSize\":\"medium\"} -->\n<h2 class=\"wp-block-heading has-medium-font-size\">Opkomende activiteiten...</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"className\":\"hideme\"} -->\n<h2 class=\"wp-block-heading hideme\">&nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<div id=\"eventslist\"></div>\n<!-- /wp:html --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:html -->\n<script>\n    // Featured event\ndocument.getElementById(\'featured-title\').innerHTML = `[events_list category=\"featured\" limit=1]#_EVENTLINK[/events_list]`\ndocument.getElementById(\'featured-image\').innerHTML = `[events_list category=\"featured\" limit=1]{has_image}#_EVENTIMAGE{/has_image}[/events_list]`\ndocument.getElementById(\'featured-excerpt\').innerHTML = `[events_list category=\"featured\" limit=1]#_EVENTEXCERPT[/events_list]`\n\n    // List of events\n    var today = new Date();\n    var future = new Date();\n    future.setDate(today.getDate() + 30);\n\n    today = today.toISOString().split(\'T\')[0];\n    future = future.toISOString().split(\'T\')[0];\n    var scope = `scope=\"${today}, ${future}\"`;\n\n    document.getElementById(\'eventslist\').innerHTML = `[events_list ${scope} category=\"-featured\" pagination=1 limit=3]{no_category_featured}<div><h4>#_EVENTLINK</h4><p>#_EVENTDATES, #_EVENTTIMES</p><p>#_EVENTEXCERPT</p></div>{/no_category_featured}[/events_list]`;\n</script>\n<!-- /wp:html -->\n\n<!-- wp:separator {\"className\":\"is-style-wide\"} -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-wide\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading {\"level\":1,\"style\":{\"typography\":{\"fontSize\":\"48px\"}}} -->\n<h1 class=\"wp-block-heading\" style=\"font-size:48px\">Het menu voor 28-08 en 30-08</h1>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"fontSize\":\"large\"} -->\n<h2 class=\"wp-block-heading has-large-font-size\">Woensdag 28-08</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Een boerenomelet met brood.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"fontSize\":\"large\"} -->\n<h2 class=\"wp-block-heading has-large-font-size\">Vrijdag 30-08</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Een lekker pita broodje met shoarma, of een lekkere kippensoep met stokbrood en kruidenboter.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"verticalAlignment\":null} -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:image {\"id\":1107,\"width\":\"350px\",\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-large is-resized\"><img src=\"https://demijngang.local/wp-content/uploads/2024/08/20240827_1-1024x491.jpg\" alt=\"\" class=\"wp-image-1107\" style=\"width:350px\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"top\"} -->\n<div class=\"wp-block-column is-vertically-aligned-top\"><!-- wp:image {\"id\":1108,\"width\":\"250px\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized\"><img src=\"https://demijngang.local/wp-content/uploads/2024/08/20240827_2.jpg\" alt=\"\" class=\"wp-image-1108\" style=\"width:250px\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"top\"} -->\n<div class=\"wp-block-column is-vertically-aligned-top\"><!-- wp:image {\"id\":1110,\"width\":\"350px\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized\"><img src=\"https://demijngang.local/wp-content/uploads/2024/08/20240827_3.jpg\" alt=\"\" class=\"wp-image-1110\" style=\"width:350px\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><em>Het menu wordt iedere dinsdag bijgewerkt.</em></p>\n<!-- /wp:paragraph -->','Home','','publish','closed','closed','','home','','','2024-08-27 12:34:49','2024-08-27 10:34:49','',0,'http://de-mijngang.local/?page_id=6',0,'page','',0);
